import React from "react";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";

// core components
import GridContainer from "components/Grid/GridContainer.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import CustomInput from "components/CustomInput/CustomInput.jsx";
import Button from "components/CustomButtons/Button.jsx";
import Select from 'react-select';
import StepLabel from '@material-ui/core/StepLabel';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';

import questionStyle from "assets/jss/material-kit-react/views/landingPageSections/questionStyle.jsx";

//Import data
import {towns, defaultChooseTown, optionsDeparment} from "../Data/Deparments";


const customStyles = {
  option: (provided, state) => ({
    ...provided,
    fontFamily: "Roboto",
    fontSize: "14px",
    color: state.isSelected ? 'white' : 'black',
    backgroundColor: state.isSelected ? 'indigo' : 'white'
  }),
  control: (provided) => ({
    ...provided,
    marginTop: "5%",
    fontFamily: "Roboto",
    fontSize: "14px",
    color:  "#495057"
  })
}

class QuestionsSection extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      height: 100,
      step: 0, 
      deparment: null,
      number: null,
      town: null,
      activeButtons: false,
    }
  }

  activeButtonsFunc(active){
    if(active&&this.state.step!=0){
      return  <div >
                <div>
                  <Button
                    disabled={this.state.step === 0}
                    onClick={this.prevStep}
                  >
                    Anterior
                  </Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={this.nextStep}
                  >
                    {this.state.step === this.getSteps().length - 1 ? 'Finalizar' : 'Siguiente'}
                  </Button>
                </div>
              </div>
    }
  }

  //Procedd next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({ step: step + 1 });
  }

  changeState = () => {
    this.nextStep()
    this.setState({ activeButtons: true });

  }

  //Go back previous step
  prevStep = () => {
    const { step } = this.state;
    this.setState({ step: step -1 });
  } 

  getSteps() {
    return ['Información personal', 'Información personal', 'Localización actual', 'Estudios y profesión'];
  }

  sectionPersonalInf(){
    return <div>
      <h5 className={this.props.classes.description}>¿La persona tiene algún sobrenombre?</h5>

      <GridContainer  justify="center">
          <GridItem xs={12} sm={12} md={4}>
            <Button
              color="success"
              onClick={this.changeState}
            >
              Si
            </Button>
            <Button
            >
              No
            </Button>
      </GridItem>
      </GridContainer>

    </div>
  }

  sectionPrimary(){
    return <div>
      <h5 className={this.props.classes.description}>¿?</h5>

      <GridContainer  justify="center">
          <GridItem xs={12} sm={12} md={4}>
            <Button
              color="success"
              onClick={this.changeState}
            >
              Si
            </Button>
            <Button
            >
              No
            </Button>
      </GridItem>
      </GridContainer>

    </div>
  }

  sectionNickname(){
    return <div>
      <CustomInput
        labelText="Sobrenombre"
        id="nickname"
        formControlProps={{
        fullWidth: true
        }}
        inputProps={{
        onChange: this.onChange,
        type: "text",
        }}
      />
      </div>
  }

  sectionActualLocal(){
    return <div>
      <Select  
        styles = {customStyles} 
        options={optionsDeparment}
        defaultValue={optionsDeparment[0]} 
        onChange={this.handleChange}
      />

      <Select  
        styles = {customStyles} 
        options={towns[this.state.number]}
        defaultValue={defaultChooseTown}
        onChange={this.handleChangeTown}
      />

      <CustomInput
        labelText={"Barrio"}
        id="neighborhood"
        formControlProps={{
        fullWidth: true
        }}
        inputProps={{
        onChange: this.onChange,
        type: "text",
        }}
      />

    </div>
  }

  getActualSection = (step) => {
    switch (step) {
      case 0:
        return this.sectionPersonalInf()
      case 1:
        return this.sectionNickname()
      case 2:
        return this.sectionActualLocal()
      default:
        return 'Unknown step';
    }
  }



  handleChange = (deparment) => {
    this.setState({ deparment });
    this.setState({ number: (optionsDeparment.indexOf(deparment)-1)});
  }

  handleChangeTown = (town) => {
    this.setState({ town });
  }


  render() {
    const { classes } = this.props;
    const { step } = this.state; 
    var { height } = this.state;

    return (
      <div className={classes.section}>
        <GridContainer justify="center">
          <GridItem cs={12} sm={12} md={8}>

            <h2 className={classes.title}>Primero, cuentanos sobre el paciente</h2>

            <p className={classes.title}>Cuentanos información sobre el paciente y algunas otras memorias que le hagan feliz.</p>
            <form>

              <GridContainer>
                <GridItem xs={12} sm={10} md={12}>

                <div >
                <Card className="shadow-lg p-3 mb-5 bg-white rounded" >
                  <CardContent>

                  <p className={classes.simpleText}> {this.getSteps()[this.state.step]}</p>
                  {this.getActualSection(this.state.step)} 

                  {this.activeButtonsFunc(this.state.activeButtons)}


                  </CardContent>
                  <CardActions>
                  </CardActions>
                </Card>
                </div>




              </GridItem>
             </GridContainer>
            </form>
          </GridItem>
        </GridContainer>
      </div>
    );
  }
}

QuestionsSection.propTypes = {
  classes: PropTypes.object
};

export default withStyles(questionStyle)(QuestionsSection);
